example.code:
@inject ContextMenuService ContextMenuService
<ElliciumButton Text="Show context menu" ContextMenu=@(args => ShowContextMenuWithContent(args)) />
@code {
    void ShowContextMenuWithContent(MouseEventArgs args) => ContextMenuService.Open(args, ds =>
      @<ElliciumMenu Click="OnMenuItemClick">
        <ElliciumMenuItem Text="Item1" Value="1"></ElliciumMenuItem>
        <ElliciumMenuItem Text="Item2" Value="2"></ElliciumMenuItem>
        <ElliciumMenuItem Text="More items" Value="3">
            <ElliciumMenuItem Text="More sub items" Value="4">
                <ElliciumMenuItem Text="Item1" Value="5"></ElliciumMenuItem>
                <ElliciumMenuItem Text="Item2" Value="6"></ElliciumMenuItem>
            </ElliciumMenuItem>
        </ElliciumMenuItem>
      </ElliciumMenu>);

    void OnMenuItemClick(MenuItemEventArgs args)
    {
      Console.WriteLine($"Menu item with Value={args.Value} clicked");
    }
}

@inject DialogService DialogService
<ElliciumButton Text="Show dialog with inline Blazor content" Click=@ShowInlineDialog />
@code {
 async Task ShowInlineDialog()
 {
   var result = await DialogService.OpenAsync("Simple Dialog", ds =>
     @<div>
         <p Style="margin-bottom: 1rem">Confirm?</p>
         <div class="row">
             <div class="col-md-12">
                 <ElliciumButton Text="Ok" Click="() => ds.Close(true)" Style="margin-bottom: 10px; width: 150px" />
                 <ElliciumButton Text="Cancel" Click="() => ds.Close(false)" ButtonStyle="ButtonStyle.Secondary"  Style="margin-bottom: 10px; width: 150px"/>
                 <ElliciumButton Text="Refresh" Click="(() => { orderID = 10249; ds.Refresh(); })" ButtonStyle="ButtonStyle.Info"  Style="margin-bottom: 10px; width: 150px"/>
                 Order ID: @orderID
             </div>
         </div>
     </div>);
   Console.WriteLine($"Dialog result: {result}");
 }
}

@inject TooltipService tooltipService
<ElliciumButton Text="Show tooltip" MouseEnter="@(args => ShowTooltipWithHtml(args, new TooltipOptions(){ Style = "color:#000", Duration = null }))" />
@code {
    void ShowTooltipWithHtml(ElementReference elementReference, TooltipOptions options = null) => tooltipService.Open(elementReference, ds =>
        @<div>
            Some<b>HTML</b> content
        </div>, options);
    }
}
